package bean;public class ItemBean{	private String name;	private String price;	private String id;	private String quantity;		public String getName(){ return name; }	public String getPrice(){ return price; }	public String getId(){ return id; }	public String getQuantity(){ return quantity; }		public void setQuantity(String quantity){ this.quantity = quantity; }	public void setId(String id){ this.id = id; }	public void setPrice(String price){ this.price = price; }	public void setName(String name){ this.name =name ; }		public String toString(){		return name;	}}