    private void AddBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddBtnActionPerformed
        // TODO add your handling code here:
    	try{
    		if(CatNameTxt.getText().length()>1){
    		DataBaseManager.addCategory(CatNameTxt.getText());
    		javax.swing.JOptionPane.showMessageDialog(null,"Category"+CatNameTxt.getText()+"has been added...");
    		}else { javax.swing.JOptionPane.showMessageDialog(this,"Improper / Empty Name"); }
    		}
    	catch(Exception e){ e.printStackTrace();}
    	finally{clear();
		listRefresh(); }
    }//GEN-LAST:event_AddBtnActionPerformed

    private void RemoveBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RemoveBtnActionPerformed
        // TODO add your handling code here:
    	if(catBean==null){ javax.swing.JOptionPane.showMessageDialog(this, " Not Selected any Category.."); return; }
  
    }//GEN-LAST:event_RemoveBtnActionPerformed

