    private void listSelectedEvent(ListSelectionEvent e){
    	if(e.getSource()==null)return;
    	catBean = (CategoryBean)Cat_List.getSelectedValue();
    	CatIdTxt.setText(""+catBean.getCatID());
    	CatNameTxt.setText(catBean.getCatName());
    }
    
