    private void clear(){
    	this.CatIdTxt.setText("");
    	this.CatNameTxt.setText("");
    }
     void listRefresh(){
    	try{
		java.util.Vector v =DataBaseManager.getCategories();
		Cat_List.setListData(v); 
    	}catch(Exception e){e.printStackTrace();}
    }
     
